-- Clear existing achievements and add the new ones
DELETE FROM public.achievements;

-- Create a table for offenders related to achievements
CREATE TABLE IF NOT EXISTS public.achievement_offenders (
  id UUID NOT NULL DEFAULT gen_random_uuid() PRIMARY KEY,
  achievement_id UUID REFERENCES public.achievements(id) ON DELETE CASCADE,
  serial_number INTEGER NOT NULL,
  name TEXT NOT NULL,
  created_at TIMESTAMP WITH TIME ZONE NOT NULL DEFAULT now()
);

-- Enable RLS for achievement_offenders
ALTER TABLE public.achievement_offenders ENABLE ROW LEVEL SECURITY;

-- Create policies for achievement_offenders
CREATE POLICY "Public read access for achievement offenders" ON public.achievement_offenders FOR SELECT USING (true);
CREATE POLICY "Admin full access achievement offenders" ON public.achievement_offenders FOR ALL USING (true);

